  SELECT LAST_INSERT_ID()

insert into shopping_cart_merchandise (shopping_cart_id, merchandise_id, merchandise_number
  )
values (#{shoppingCartId,jdbcType=BIGINT}, #{merchandiseId,jdbcType=BIGINT}, #{merchandiseNumber,jdbcType=INTEGER}
  )

mapper.update:
